// Safely extract and validate inbound webhook payload
const body = $json.body || $json || {};
const problemId = body.problem_id;

if (!problemId) {
  return [{ json: { route: 'error', error: 'missing_problem_id', raw: body } }];
}

return [{
  json: {
    route: 'ok',
    problem_id: String(problemId),
    title: body.title || 'Unknown Problem',
    priority: body.priority || body.severity || 'high',
    affected_tickets_count: body.affected_tickets_count || 0
  }
}];

// Merge inbound context with enriched backend data
// Problem model has no dedicated description field

const ctx = $node['Store Inbound Context'].json;
const detail = $json || {};

if (detail.error || detail.statusCode >= 400) {
  return [{ json: { route: 'error', error: detail.error || 'backend_fetch_failed', status: detail.statusCode } }];
}

const problemId = ctx.problem_id;
const uiBase = $env.FRONTEND_BASE_URL || 'http://localhost:3000';

return [{
  json: {
    route: 'ok',
    problem_id: problemId,
    title: detail.title || ctx.inbound_title || 'Problem Detected',
    priority: detail.priority || ctx.inbound_priority || detail.severity || 'high',
    status: detail.status || 'open',
    affected_tickets_count: detail.affected_tickets_count ?? ctx.inbound_affected_count ?? 0,
    root_cause: detail.root_cause || 'Under investigation',
    workaround: detail.workaround || 'None yet',
    permanent_fix: detail.permanent_fix || 'None yet',
    created_at: detail.created_at || new Date().toISOString(),
    problem_url: `${uiBase}/problems/${problemId}`,
    raw_detail: detail
  }
}];

const d = $json;
const sep = '------------------------------------------';

const rawPriority = d.priority || d.raw_detail?.priority || d.inbound_priority || d.raw_detail?.severity || 'high';
const pri = String(rawPriority).trim() || 'high';

const tickets = Array.isArray(d.raw_detail?.tickets) ? d.raw_detail.tickets : [];
const topTickets = tickets.slice(0, 5);

const summaryBits = [];
summaryBits.push(`${d.title || 'Problem detected'} is currently ${d.status || 'open'}.`);
summaryBits.push(`Impact priority is ${pri.toUpperCase()} with ${d.affected_tickets_count ?? tickets.length ?? 0} related ticket(s).`);

if (topTickets.length) {
  const statuses = [...new Set(topTickets.map(t => t.status).filter(Boolean))];
  if (statuses.length) summaryBits.push(`Linked ticket statuses: ${statuses.join(', ')}.`);
  const sample = topTickets.map(t => t.title).filter(Boolean).slice(0,2).join(' | ');
  if (sample) summaryBits.push(`Observed symptoms: ${sample}.`);
}

if (d.root_cause && d.root_cause !== 'Under investigation') {
  summaryBits.push(`Likely root cause: ${d.root_cause}.`);
} else {
  summaryBits.push('Root cause is still under investigation.');
}

if (d.workaround && d.workaround !== 'None yet.') {
  summaryBits.push(`Current workaround: ${d.workaround}.`);
}

const generatedSummary = summaryBits.join(' ');

const ticketLines = topTickets.length
  ? topTickets.map(t => `- ${t.id || 'N/A'} | ${t.title || 'Untitled'} | ${t.status || 'unknown'} | ${t.assignee || '-'}`).join('\n')
  : '- None listed';

const subject = `[ITSM Problem] ${pri.toUpperCase()} - ${d.title || 'Problem Detected'}`;

const body = [
  'ITSM Platform - Problem Notification',
  '==========================================',
  '',
  `Problem ID   : ${d.problem_id || 'N/A'}`,
  `Title        : ${d.title || 'N/A'}`,
  `Priority     : ${pri.toUpperCase()}`,
  `Status       : ${d.status || 'open'}`,
  `Affected     : ${d.affected_tickets_count ?? 0} ticket(s)`,
  `Detected at  : ${d.created_at || new Date().toISOString()}`,
  '',
  sep,
  'Problem Summary',
  sep,
  generatedSummary,
  '',
  sep,
  'Root Cause',
  sep,
  d.root_cause || 'Under investigation.',
  '',
  sep,
  'Workaround',
  sep,
  d.workaround || 'None yet.',
  '',
  sep,
  'Permanent Fix',
  sep,
  d.permanent_fix || 'None yet.',
  '',
  sep,
  'Linked Tickets (top 5)',
  sep,
  ticketLines,
  '',
  sep,
  'View in ITSM Platform:',
  d.problem_url || 'N/A',
  sep,
  '',
  'This message was generated automatically by the ITSM n8n automation layer.',
  'Actor: system:n8n'
].join('\n');

return [{ json: { ...d, priority: pri, email_subject: subject, email_body: body, generated_summary: generatedSummary } }];

// Guard: ignore accidentally routed success payloads
if ($json && $json.route === 'ok') {
  return [];
}

const stage = $runIndex === 0 ? 'unknown' : 'execution';
const payload = {
  workflow: 'Problem Launch Notifier',
  timestamp: new Date().toISOString(),
  stage,
  error: $json.error || $json.message || JSON.stringify($json)
};
console.error('[n8n][problem-notifier][error]', JSON.stringify(payload));
return [{ json: payload }];
